mapper.select:
SELECT COUNT(*) 
FROM N_DENPYO
WHERE KOUNYU_DATE = #{kounyuDate}

mapper.insert:
INSERT INTO N_DENPYO (
	  DENPYO_NO
	, CUSTOMER_CD
	, KOUNYU_DATE
	, ZEINUKIGAKU
	, ZEIGAKU
	, ZEIKOMIGAKU
	, HAKKOZUMI_FLAG
	, CREATE_DATE
	, UPDATE_DATE
) VALUES (
	  #{denpyoNo}
	, #{customerCd}
	, #{kounyuDate}
	, #{zeinukiGaku}
	, #{zeiGaku}
	, #{zeikomiGaku}
	, '0'
	, CURRENT_TIMESTAMP
	, CURRENT_TIMESTAMP
)
